import { Box } from "@mui/material";
import React, { useState } from "react";
import ArrowBackIosRoundedIcon from "@mui/icons-material/ArrowBackIosRounded";
import ArrowForwardIosRoundedIcon from "@mui/icons-material/ArrowForwardIosRounded";
import "../../../cssModules/StuDashboard.css";
import CourseBox from "../../../components/CourseBox";

const boxShadow = "0px 4px 4px 0px rgba(0, 0, 0, 0.25)";

const AccessedCourses = [
  {
    title: "English Language",
    subtitle: "First Semester",
    image:
      "data:image/svg+xml;base64,",
  },
  {
    title: "Mathematics",
    subtitle: "First Semester",
    image:
      "data:image/svg+xml;base64,",
  },
  {
    title: "Social Studies",
    subtitle: "First Semester",
    image:
      "data:image/svg+xml;base64,",
  },
];

function StudentDashBoard() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    } else {
      setCurrentIndex(AccessedCourses.length - 1);
    }
  };

  const handleNext = () => {
    if (currentIndex < AccessedCourses.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setCurrentIndex(0);
    }
  };

  return (
    <Box display="flex" m="20px" justifyContent="space-between">
      <Box display="flex" width="68%" flexDirection="column">
        <Box
          justifyContent="center"
          borderRadius="0.625rem"
          bgcolor="white"
          height="10rem"
          width="100%"
          display="flex"
          boxShadow={boxShadow}
        >
          <p style={{ margin: "0px", alignSelf: "center" }}>
            No new quiz or exams posted
          </p>
        </Box>
        <Box
          mt="20px"
          bgcolor="white"
          borderRadius="0.625rem"
          height="min-content"
          minHeight="20rem"
          boxShadow={boxShadow}
          padding="10px"
          position="relative"
        >
          <p style={{ margin: "0px" }}>Recently Accessed Subjects</p>
          <Box display="flex">
            <button
              className="img-slider-btn"
              style={{ left: 0 }}
              onClick={handlePrev}
            >
              <ArrowBackIosRoundedIcon />
            </button>
            <button
              className="img-slider-btn"
              style={{ right: 0 }}
              onClick={handleNext}
            >
              <ArrowForwardIosRoundedIcon />
            </button>
          </Box>
          <Box
            display="flex"
            height="100%"
            alignItems="center"
            justifyContent="center"
            // width="100%"
            position="absolute"
            left="32%"
          >
            {AccessedCourses.map((courses, index) => {
              return (
                <Box
                  key={index}
                  sx={{
                    transform: `translateX(0%)`,
                    transition: "transform 0.3s ease-in-out",
                    width: "100%",
                  }}
                >
                  {index === currentIndex && (
                    <CourseBox
                      key={index}
                      title={courses.title}
                      subtitle={courses.subtitle}
                      image={courses.image}
                    />
                  )}
                </Box>
              );
            })}
          </Box>
        </Box>
      </Box>
      <Box
        bgcolor="white"
        borderRadius="0.625rem"
        width="30%"
        boxShadow={boxShadow}
        minHeight="15rem"
        height="min-content"
      ></Box>
    </Box>
  );
}

export default StudentDashBoard;
